mapper.select:
select isbn, title, author, price, content, joindate
from book
where isbn = #{isbn}

mapper.insert:
insert into book(
	isbn, title, author, price, content, joindate
) values (
	#{isbn}, #{title}, #{author}, #{price}, #{content}, now()
)

mapper.update:

update book
set title = #{title}, 
	author = #{author}, 
	price = #{price},
	content = #{content}
 where isbn = #{isbn}
